import React, { useState, useEffect } from "react";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  
  useEffect(() => {
    // Fetch cart items from localStorage or API
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(storedCart);
  }, []);

  const handleRemoveItem = (itemId) => {
    const updatedCart = cartItems.filter(item => item.id !== itemId);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // Save the updated cart
  };

  const handleCheckout = () => {
    // Proceed to checkout page or order placement logic
    console.log("Proceeding to checkout...");
  };

  return (
    <div className="max-w-4xl mx-auto py-10">
      <h2 className="text-3xl font-semibold mb-6">Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id} className="flex justify-between mb-4">
              <span>{item.title}</span>
              <button onClick={() => handleRemoveItem(item.id)} className="text-red-500">
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
      <button onClick={handleCheckout} className="bg-blue-600 text-white px-4 py-2 rounded">
        Proceed to Checkout
      </button>
    </div>
  );
};

export default Cart;
