import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const BookCatalog = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [bookmarks, setBookmarks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`https://localhost:7126/api/Books?page=${page}`);
        setBooks(response.data.books);
        setTotalPages(response.data.totalPages);
      } catch (error) {
        console.error('Error fetching books:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchBooks();
  }, [page]);

  const handleAddToCart = (book) => {
    // Add book to cart (for simplicity, storing in local state)
    alert(`${book.title} added to cart`);
  };

  const handleBookmark = async (book) => {
    try {
      // Add a bookmark for the current user
      const response = await axios.post('https://localhost:7126/api/Bookmarks', { bookId: book.id });
      setBookmarks([...bookmarks, book]);
      alert(`${book.title} added to your bookmarks!`);
    } catch (error) {
      console.error("Error adding bookmark:", error);
      alert("Failed to add bookmark.");
    }
  };

  const handleViewDetails = (bookId) => {
    navigate(`/book-details/${bookId}`);
  };

  return (
    <div className="max-w-6xl mx-auto py-10">
      <h2 className="text-3xl font-semibold mb-6">Book Catalog</h2>
      {loading ? (
        <div className="text-center">Loading...</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {books.map((book) => (
            <div key={book.id} className="border rounded-lg p-4">
              <img src={`https://localhost:7126${book.coverImageUrl}`} alt={book.title} className="w-full h-60 object-cover mb-4" />
              <h3 className="text-xl font-medium">{book.title}</h3>
              <p className="text-gray-600">{book.author}</p>
              <div className="flex justify-between items-center mt-4">
                <button
                  onClick={() => handleViewDetails(book.id)}
                  className="text-blue-600 hover:text-blue-800"
                >
                  View Details
                </button>
                <button
                  onClick={() => handleAddToCart(book)}
                  className="bg-blue-600 text-white px-4 py-2 rounded"
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => handleBookmark(book)}
                  className="bg-yellow-500 text-white px-4 py-2 rounded"
                >
                  Bookmark
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className="flex justify-center mt-6">
        <button
          onClick={() => setPage(Math.max(1, page - 1))}
          className="bg-gray-200 px-4 py-2 rounded mx-2"
        >
          Previous
        </button>
        <button
          onClick={() => setPage(Math.min(totalPages, page + 1))}
          className="bg-gray-200 px-4 py-2 rounded mx-2"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default BookCatalog;
