import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

// Create Context
export const CartContext = createContext();

// Provide context to children
export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchCartItems = async () => {
    setIsLoading(true);
    try {
      const storedUser = localStorage.getItem('user');
      if (!storedUser) {
        setCartItems([]);
        setIsLoading(false);
        return;
      }
  
      // Call the correct endpoint as defined in your CartController
      const response = await axios.get('https://localhost:7126/api/Cart', {
        withCredentials: true
      });
      
      setCartItems(response.data.items || []);
      setIsLoading(false);
    } catch (err) {
      setError(err.message || 'Error fetching cart items');
      setIsLoading(false);
    }
  };

  const addToCart = async ({bookId, quantity}) => {
    try {
      const storedUser = localStorage.getItem('user');
      if (!storedUser) {
        throw new Error('User is not logged in');
      }
      
      const data = {
        bookId: bookId,
        quantity: quantity,
      };
      
      const response = await axios.post(
        'https://localhost:7126/api/Cart/add',
        data,
        { withCredentials: true }
      );
      
      // Update local cart state
      fetchCartItems();
      return response.data;
    } catch (err) {
      setError(err.message || 'Error adding item to cart');
      throw err;
    }
  };
  

  // Remove an item from the cart
  const removeItem = async (bookId) => {
    try {
      const storedUser = localStorage.getItem('user');
      if (!storedUser) {
        throw new Error('User is not logged in');
      }

      const user = JSON.parse(storedUser);
      const userId = user.id;

      const response = await axios.delete(
        `https://localhost:7126/api/Cart/${bookId}`,
        { data: { userId } }
      );

      setCartItems((prevItems) => prevItems.filter((item) => item.id !== bookId));
    } catch (err) {
      setError(err.message || 'Error removing item from cart');
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  return (
    <CartContext.Provider value={{ cartItems, isLoading, error, addToCart, removeItem }}>
      {children}
    </CartContext.Provider>
  );
};

// Custom hook to use Cart Context
export const useCart = () => {
  return useContext(CartContext);
};
