import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Star, ShoppingCart, Bookmark, Heart, Share2, ChevronLeft, Award, Clock, ThumbsUp, ChevronDown } from "lucide-react";
import { UserContext } from "../contexts/UserContext";
import { CartContext } from "../contexts/CartContext";

export default function EnhancedBookDetails() {
  const { bookId } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(UserContext);
  const { addItem } = useContext(CartContext);
  
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [userReview, setUserReview] = useState(null);
  const [reviewText, setReviewText] = useState("");
  const [reviewRating, setReviewRating] = useState(0);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewFormLoading, setReviewFormLoading] = useState(false);
  const [reviewFormError, setReviewFormError] = useState("");
  const [expandedReviews, setExpandedReviews] = useState(false);
  const [addedToCart, setAddedToCart] = useState(false);
  
  // Fetch book details
  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://localhost:7126/api/Books/${bookId}`);
        
        if (!response.ok) {
          throw new Error('Failed to fetch book details');
        }
        
        const bookData = await response.json();
        setBook(bookData);
        
        // If user is logged in, check if book is bookmarked
        if (user) {
          checkBookmarkStatus();
          checkUserReview();
        }
      } catch (err) {
        console.error('Error fetching book details:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    
    fetchBookDetails();
  }, [bookId, user]);
  
  // Check if book is bookmarked by the user
  const checkBookmarkStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) return;
      
      const response = await fetch('https://localhost:7126/api/Bookmarks', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch bookmarks');
      }
      
      const bookmarks = await response.json();
      const isMarked = bookmarks.some(bookmark => bookmark.id === parseInt(bookId));
      setIsBookmarked(isMarked);
    } catch (err) {
      console.error('Error checking bookmark status:', err);
    }
  };
  
  // Check if user has reviewed this book
  const checkUserReview = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) return;
      
      const response = await fetch('https://localhost:7126/api/Reviews/user', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch user reviews');
      }
      
      const reviews = await response.json();
      const review = reviews.find(r => r.bookId === parseInt(bookId));
      
      if (review) {
        setUserReview(review);
        setReviewText(review.comment);
        setReviewRating(review.rating);
      }
    } catch (err) {
      console.error('Error checking user review:', err);
    }
  };
  
  // Toggle bookmark status
  const toggleBookmark = async () => {
    if (!user) {
      navigate('/login', { state: { redirectTo: `/book/${bookId}` } });
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Not authenticated');
      }
      
      if (isBookmarked) {
        // Remove bookmark
        const response = await fetch(`https://localhost:7126/api/Bookmarks/${bookId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        if (!response.ok) {
          throw new Error('Failed to remove bookmark');
        }
        
        setIsBookmarked(false);
      } else {
        // Add bookmark
        const response = await fetch('https://localhost:7126/api/Bookmarks', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(parseInt(bookId))
        });
        
        if (!response.ok) {
          throw new Error('Failed to add bookmark');
        }
        
        setIsBookmarked(true);
      }
    } catch (err) {
      console.error('Error toggling bookmark:', err);
      // Display error message to user
    }
  };
  
  // Add book to cart
  const handleAddToCart = () => {
    if (!book) return;
    
    addItem({
      id: book.id,
      title: book.title,
      author: book.author,
      price: book.price,
      imageUrl: book.imageUrl
    });
    
    // Show success indicator
    setAddedToCart(true);
    setTimeout(() => {
      setAddedToCart(false);
    }, 2000);
  };
  
  // Submit review
  const handleSubmitReview = async (e) => {
    e.preventDefault();
    
    if (!user) {
      navigate('/login', { state: { redirectTo: `/book/${bookId}` } });
      return;
    }
    
    if (reviewRating === 0) {
      setReviewFormError("Please select a rating");
      return;
    }
    
    try {
      setReviewFormLoading(true);
      setReviewFormError("");
      
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Not authenticated');
      }
      
      const reviewData = {
        bookId: parseInt(bookId),
        rating: reviewRating,
        comment: reviewText
      };
      
      let response;
      
      if (userReview) {
        // Update existing review
        reviewData.id = userReview.id;
        
        response = await fetch(`https://localhost:7126/api/Reviews/${userReview.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(reviewData)
        });
      } else {
        // Create new review
        response = await fetch('https://localhost:7126/api/Reviews', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(reviewData)
        });
      }
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to submit review');
      }
      
      // Refresh book details to show updated review
      const updatedBookResponse = await fetch(`https://localhost:7126/api/Books/${bookId}`);
      if (updatedBookResponse.ok) {
        const updatedBook = await updatedBookResponse.json();
        setBook(updatedBook);
      }
      
      // If this was a new review, update userReview state
      if (!userReview) {
        setUserReview({
          id: parseInt(bookId), // This will be replaced with actual ID from response
          bookId: parseInt(bookId),
          rating: reviewRating,
          comment: reviewText,
          createdAt: new Date().toISOString()
        });
      }
      
      setShowReviewForm(false);
    } catch (err) {
      console.error('Error submitting review:', err);
      setReviewFormError(err.message);
    } finally {
      setReviewFormLoading(false);
    }
  };
  
  // Delete review
  const handleDeleteReview = async () => {
    if (!userReview) return;
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Not authenticated');
      }
      
      const response = await fetch(`https://localhost:7126/api/Reviews/${userReview.id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        throw new Error('Failed to delete review');
      }
      
      // Reset review states
      setUserReview(null);
      setReviewText("");
      setReviewRating(0);
      setShowReviewForm(false);
      
      // Refresh book details to show updated reviews
      const updatedBookResponse = await fetch(`https://localhost:7126/api/Books/${bookId}`);
      if (updatedBookResponse.ok) {
        const updatedBook = await updatedBookResponse.json();
        setBook(updatedBook);
      }
    } catch (err) {
      console.error('Error deleting review:', err);
      // Display error message to user
    }
  };
  
  // Render stars for rating
  const renderStars = (rating, interactive = false) => {
    return Array(5)
      .fill(0)
      .map((_, i) => (
        <Star
          key={i}
          size={interactive ? 24 : 18}
          onClick={interactive ? () => setReviewRating(i + 1) : undefined}
          className={`${interactive ? 'cursor-pointer' : ''} ${
            i < Math.floor(rating) ? 'text-yellow-400 fill-yellow-400' : 'text-gray-300'
          } ${interactive && i < reviewRating ? 'text-yellow-400 fill-yellow-400' : ''}`}
        />
      ));
  };
  
  // Calculate half stars for more accurate ratings
  const hasHalfStar = (rating) => rating % 1 >= 0.5;
  
  // Calculate discounted price if book is on sale
  const getDisplayPrice = () => {
    if (!book) return null;
    
    if (book.isOnSale && book.discountPercentage && 
        book.discountStartDate && book.discountEndDate) {
      const now = new Date();
      const startDate = new Date(book.discountStartDate);
      const endDate = new Date(book.discountEndDate);
      
      if (now >= startDate && now <= endDate) {
        const discountedPrice = book.price * (1 - book.discountPercentage / 100);
        return {
          current: discountedPrice.toFixed(2),
          original: book.price.toFixed(2),
          discount: book.discountPercentage
        };
      }
    }
    
    return {
      current: book.price.toFixed(2),
      original: null,
      discount: null
    };
  };
  
  if (loading) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-red-50 text-red-600 p-4 rounded-lg">
          <p>Error: {error}</p>
          <button
            onClick={() => navigate('/browse')}
            className="mt-4 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
          >
            Return to Browse
          </button>
        </div>
      </div>
    );
  }
  
  if (!book) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-yellow-50 text-yellow-600 p-4 rounded-lg">
          <p>Book not found.</p>
          <button
            onClick={() => navigate('/browse')}
            className="mt-4 bg-yellow-600 text-white px-4 py-2 rounded-lg hover:bg-yellow-700 transition-colors"
          >
            Return to Browse
          </button>
        </div>
      </div>
    );
  }
  
  const price = getDisplayPrice();
  
  return (
    <div className="max-w-7xl mx-auto bg-white rounded-xl shadow-md overflow-hidden">
      {/* Breadcrumb navigation */}
      <div className="px-6 pt-6 pb-2">
        <button
          onClick={() => navigate('/browse')}
          className="inline-flex items-center text-sm text-purple-600 hover:text-purple-800 transition-colors"
        >
          <ChevronLeft size={16} className="mr-1" />
          Back to Books
        </button>
      </div>

      <div className="p-6">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Book image with badges */}
          <div className="lg:w-1/3 relative">
            <div className="aspect-[3/4] rounded-xl overflow-hidden bg-gray-100 shadow-lg">
              <img
                src={book.imageUrl || "/placeholder.svg?height=600&width=400&query=book cover"}
                alt={`${book.title} book cover`}
                className="w-full h-full object-cover"
              />
            </div>

            {/* Badges */}
            <div className="absolute top-4 left-4 flex flex-col gap-2">
              {book.isBestseller && (
                <span className="bg-yellow-500 text-white text-xs font-bold px-3 py-1.5 rounded-full flex items-center">
                  <Award size={14} className="mr-1" />
                  Bestseller
                </span>
              )}
              {book.isNewRelease && (
                <span className="bg-purple-600 text-white text-xs font-bold px-3 py-1.5 rounded-full flex items-center">
                  <Clock size={14} className="mr-1" />
                  New Release
                </span>
              )}
              {book.isOnSale && price.discount && (
                <span className="bg-red-500 text-white text-xs font-bold px-3 py-1.5 rounded-full flex items-center">
                  <span className="mr-1">{price.discount}%</span>
                  On Sale
                </span>
              )}
            </div>

            {/* Share button */}
            <button 
              className="absolute top-4 right-4 bg-white/80 backdrop-blur-sm p-2 rounded-full shadow-md hover:bg-white transition-colors"
              onClick={() => navigator.share({
                title: book.title,
                text: `Check out ${book.title} by ${book.author}`,
                url: window.location.href
              }).catch(err => console.error('Error sharing:', err))}
            >
              <Share2 size={18} className="text-gray-700" />
            </button>
          </div>

          {/* Book details */}
          <div className="lg:w-2/3">
            <div className="flex flex-col h-full">
              <div className="flex-grow">
                <div className="flex flex-wrap items-start justify-between gap-4 mb-4">
                  <div>
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">{book.title}</h1>
                    <p className="text-xl text-gray-600">
                      by <span className="hover:text-purple-600 cursor-pointer transition-colors">{book.author}</span>
                    </p>
                  </div>

                  <div className="flex items-center gap-3">
                    <span className="text-2xl font-bold text-purple-600">
                      ${price.current}
                    </span>
                    {price.original && price.original !== price.current && (
                      <span className="text-lg text-gray-500 line-through">${price.original}</span>
                    )}
                    <span
                      className={`text-sm px-3 py-1 rounded-full font-medium ${
                        book.stock > 0 ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"
                      }`}
                    >
                      {book.stock > 0 ? "In stock" : "Out of stock"}
                    </span>
                  </div>
                </div>

                <div className="flex items-center mb-6">
                  <div className="flex">
                    {renderStars(book.averageRating || 0)}
                    {hasHalfStar(book.averageRating || 0) && (
                      <div className="relative">
                        <Star size={18} className="text-gray-300" />
                        <div className="absolute inset-0 overflow-hidden w-1/2">
                          <Star size={18} className="text-yellow-400 fill-yellow-400" />
                        </div>
                      </div>
                    )}
                  </div>
                  <span className="ml-2 text-gray-600">({book.averageRating?.toFixed(1) || 0} out of 5)</span>
                  <span className="mx-2 text-gray-400"></span>
                  <a href="#reviews" className="text-purple-600 hover:text-purple-800 transition-colors">
                    {book.reviewCount || 0} reviews
                  </a>
                </div>

                <div className="mb-8">
                  <h3 className="text-lg font-semibold mb-3 text-gray-900">Description</h3>
                  <p className="text-gray-700 leading-relaxed">
                    {book.description || "No description available for this book."}
                  </p>
                </div>
              </div>

              {/* Action buttons */}
              <div className="flex flex-wrap gap-3 mb-6">
                <button 
                  className={`flex items-center gap-2 ${
                    addedToCart 
                      ? "bg-green-600 text-white"
                      : "bg-purple-600 text-white hover:bg-purple-700"
                  } px-6 py-3 rounded-lg transition-colors shadow-sm font-medium`}
                  onClick={handleAddToCart}
                  disabled={book.stock <= 0 || addedToCart}
                >
                  <ShoppingCart size={18} />
                  <span>
                    {book.stock <= 0 
                      ? "Out of Stock" 
                      : addedToCart 
                        ? "Added to Cart!" 
                        : "Add to Cart"}
                  </span>
                </button>
                <button 
                  className={`flex items-center gap-2 ${
                    isBookmarked ? 
                      "bg-purple-100 text-purple-800 hover:bg-purple-200" : 
                      "bg-gray-100 text-gray-800 hover:bg-gray-200"
                  } px-6 py-3 rounded-lg transition-colors font-medium`}
                  onClick={toggleBookmark}
                >
                  <Bookmark size={18} className={isBookmarked ? "fill-purple-800" : ""} />
                  <span>{isBookmarked ? "Bookmarked" : "Bookmark"}</span>
                </button>
                <button className="flex items-center gap-2 bg-red-50 text-red-600 px-6 py-3 rounded-lg hover:bg-red-100 transition-colors font-medium">
                  <Heart size={18} />
                  <span>Add to Wishlist</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Book details section */}
      <div className="border-t border-gray-100 bg-gray-50 p-8 rounded-b-xl">
        <h2 className="text-xl font-semibold mb-6 text-gray-900">Book Details</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-y-4 gap-x-8">
          <div className="space-y-3">
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Genre:</span>
              <span className="text-gray-900">{book.genre || "Unknown"}</span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Format:</span>
              <span className="text-gray-900">{book.format || "Paperback"}</span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Pages:</span>
              <span className="text-gray-900">{book.pages || "Unknown"}</span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Language:</span>
              <span className="text-gray-900">{book.language || "English"}</span>
            </p>
          </div>
          <div className="space-y-3">
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Publisher:</span>
              <span className="text-gray-900">{book.publisher || "Unknown"}</span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Publication Date:</span>
              <span className="text-gray-900">
                {book.publishDate ? new Date(book.publishDate).toLocaleDateString() : "Unknown"}
              </span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">ISBN:</span>
              <span className="text-gray-900">{book.isbn || "Unknown"}</span>
            </p>
            <p className="flex justify-between">
              <span className="text-gray-600 font-medium">Dimensions:</span>
              <span className="text-gray-900">{book.dimensions || "Unknown"}</span>
            </p>
          </div>
        </div>
      </div>

      {/* Reviews section */}
      <div id="reviews" className="border-t border-gray-100 p-8">
        <div className="flex flex-wrap items-center justify-between gap-4 mb-6">
          <h2 className="text-xl font-semibold text-gray-900">Customer Reviews</h2>
          
          {user && !userReview && !showReviewForm && (
            <button
              onClick={() => setShowReviewForm(true)}
              className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
            >
              Write a Review
            </button>
          )}
        </div>
        
        {/* Review form */}
        {user && showReviewForm && (
          <div className="bg-gray-50 p-6 rounded-lg mb-6">
            <h3 className="font-semibold text-gray-900 mb-4">
              {userReview ? "Edit Your Review" : "Write a Review"}
            </h3>
            
            {reviewFormError && (
              <div className="bg-red-50 text-red-600 p-3 rounded-lg mb-4">
                {reviewFormError}
              </div>
            )}
            
            <form onSubmit={handleSubmitReview} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Rating <span className="text-red-500">*</span>
                </label>
                <div className="flex items-center gap-1">
                  {renderStars(reviewRating, true)}
                </div>
              </div>
              
              <div>
                <label htmlFor="reviewText" className="block text-sm font-medium text-gray-700 mb-1">
                  Review
                </label>
                <textarea
                  id="reviewText"
                  rows={4}
                  value={reviewText}
                  onChange={(e) => setReviewText(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                  placeholder="Share your thoughts about this book..."
                ></textarea>
              </div>
              
              <div className="flex gap-3">
                <button
                  type="submit"
                  disabled={reviewFormLoading}
                  className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
                >
                  {reviewFormLoading ? "Submitting..." : userReview ? "Update Review" : "Submit Review"}
                </button>
                
                <button
                  type="button"
                  onClick={() => setShowReviewForm(false)}
                  className="bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors"
                >
                  Cancel
                </button>
                
                {userReview && (
                  <button
                    type="button"
                    onClick={handleDeleteReview}
                    className="bg-red-50 text-red-600 px-4 py-2 rounded-lg hover:bg-red-100 transition-colors ml-auto"
                  >
                    Delete Review
                  </button>
                )}
              </div>
            </form>
          </div>
        )}
        
        {/* User's review */}
        {user && userReview && !showReviewForm && (
          <div className="bg-purple-50 p-6 rounded-lg mb-6 border border-purple-100">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-semibold text-gray-900 mb-1">Your Review</h3>
                <div className="flex items-center gap-3 mb-2">
                  <div className="flex">
                    {renderStars(userReview.rating)}
                  </div>
                  <span className="text-sm text-gray-500">
                    {new Date(userReview.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
              
              <div className="flex gap-2">
                <button
                  onClick={() => setShowReviewForm(true)}
                  className="text-purple-600 hover:text-purple-800 text-sm"
                >
                  Edit
                </button>
                <button
                  onClick={handleDeleteReview}
                  className="text-red-600 hover:text-red-800 text-sm"
                >
                  Delete
                </button>
              </div>
            </div>
            
            <p className="text-gray-700">{userReview.comment}</p>
          </div>
        )}
        
        {/* Other reviews */}
        {book.reviews && book.reviews.length > 0 ? (
          <div className="space-y-6">
            {(expandedReviews ? book.reviews : book.reviews.slice(0, 3)).map((review, index) => (
              <div key={index} className="border-b border-gray-100 pb-6 last:border-b-0">
                <div className="flex items-start justify-between">
                  <div>
                    <div className="flex items-center gap-3 mb-2">
                      <div className="flex">
                        {renderStars(review.rating)}
                      </div>
                      <span className="font-medium">{review.userName}</span>
                      <span className="text-sm text-gray-500">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                  
                  <button className="text-gray-400 hover:text-gray-600">
                    <ThumbsUp size={16} />
                  </button>
                </div>
                
                <p className="text-gray-700">{review.comment}</p>
              </div>
            ))}
            
            {book.reviews.length > 3 && (
              <button
                onClick={() => setExpandedReviews(!expandedReviews)}
                className="flex items-center gap-1 text-purple-600 hover:text-purple-800 font-medium"
              >
                {expandedReviews ? (
                  <>
                    Show Less
                    <ChevronDown size={18} className="transform rotate-180" />
                  </>
                ) : (
                  <>
                    Show All Reviews ({book.reviews.length})
                    <ChevronDown size={18} />
                  </>
                )}
              </button>
            )}
          </div>
        ) : (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <p className="text-gray-500">No reviews yet. Be the first to review this book!</p>
            
            {!user && (
              <button
                onClick={() => navigate('/login', { state: { redirectTo: `/book/${bookId}` } })}
                className="mt-4 bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
              >
                Login to Review
              </button>
            )}
          </div>
        )}
      </div>

      {/* Recommendations section */}
      {book.relatedBooks && book.relatedBooks.length > 0 && (
        <div className="border-t border-gray-100 p-8">
          <h2 className="text-xl font-semibold mb-6 text-gray-900">You might also like</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {book.relatedBooks.map((relatedBook) => (
              <div key={relatedBook.id} className="group cursor-pointer" onClick={() => navigate(`/book/${relatedBook.id}`)}>
                <div className="aspect-[3/4] rounded-lg overflow-hidden bg-gray-100 mb-2 group-hover:shadow-md transition-shadow">
                  <img
                    src={relatedBook.imageUrl || `/abstract-book-cover.png?height=300&width=200&query=book cover ${relatedBook.id}`}
                    alt={`${relatedBook.title} book cover`}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                  />
                </div>
                <h3 className="font-medium text-gray-900 group-hover:text-purple-600 transition-colors">
                  {relatedBook.title}
                </h3>
                <p className="text-sm text-gray-600">{relatedBook.author}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Recently viewed books section - if implemented */}
      {/* <div className="border-t border-gray-100 p-8">
        <h2 className="text-xl font-semibold mb-6 text-gray-900">Recently Viewed</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {recentlyViewedBooks.map((book) => (
            <div key={book.id} className="group cursor-pointer" onClick={() => navigate(`/book/${book.id}`)}>
              <div className="aspect-[3/4] rounded-md overflow-hidden bg-gray-100 mb-2">
                <img
                  src={book.imageUrl || `/abstract-book-cover.png?height=200&width=140&query=book cover ${book.id}`}
                  alt={`${book.title} book cover`}
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                />
              </div>
              <h3 className="text-sm font-medium text-gray-900 truncate group-hover:text-purple-600 transition-colors">
                {book.title}
              </h3>
            </div>
          ))}
        </div>
      </div> */}

      {/* Toast notification for added to cart */}
      {addedToCart && (
        <div className="fixed bottom-4 right-4 bg-green-600 text-white px-4 py-2 rounded-lg shadow-lg flex items-center gap-2 z-50 animate-fade-in-up">
          <ShoppingCart size={18} />
          <span>Added to cart!</span>
        </div>
      )}
    </div>
  );
}
