import { useState, useEffect } from "react";
import { ChevronDown, X, Star, Search } from "lucide-react";

export default function Filters({ onChange, initialFilters = {} }) {
  const [filters, setFilters] = useState({
    search: initialFilters.search || "",
    genre: initialFilters.genre || "",
    priceRange: initialFilters.priceRange || [0, 100],
    rating: initialFilters.rating || 0,
    availability: initialFilters.availability || { inStock: false },
    sort: initialFilters.sort || "title", // default sorting by title
  });

  // UI states
  const [openDropdown, setOpenDropdown] = useState(null);

  // Update parent component when filters change
  useEffect(() => {
    onChange(filters); // Pass the filters to the parent component

  }, [filters, onChange]);

  const handleSearchChange = (e) => {
    setFilters({ ...filters, search: e.target.value });
  };

  const handleSortChange = (e) => {
    setFilters({ ...filters, sort: e.target.value });
  };

  const handlePriceChange = (e) => {
    const value = Number.parseInt(e.target.value);
    const newRange = [...filters.priceRange];
    if (e.target.id === "min-price") {
      newRange[0] = value;
    } else {
      newRange[1] = value;
    }
    setFilters({ ...filters, priceRange: newRange });
  };

  const handleRatingChange = (value) => {
    setFilters({ ...filters, rating: value });
  };

  const handleAvailabilityChange = () => {
    setFilters({
      ...filters,
      availability: { inStock: !filters.availability.inStock },
    });
  };

  const resetFilters = () => {
    setFilters({
      search: "",
      genre: "",
      priceRange: [0, 100],
      rating: 0,
      availability: { inStock: false },
      sort: "title",
    });
  };

  const renderStarRating = () => {
    return (
      <div className="flex items-center gap-2 mb-2">
        {[1, 2, 3, 4, 5].map((value) => (
          <button
            key={value}
            onClick={() => handleRatingChange(value)}
            className="focus:outline-none"
          >
            <Star
              size={20}
              className={`${
                value <= filters.rating
                  ? "text-yellow-400 fill-yellow-400"
                  : "text-gray-300"
              } hover:text-yellow-400 transition-colors`}
            />
          </button>
        ))}
        {filters.rating > 0 && (
          <button
            onClick={() => handleRatingChange(0)}
            className="ml-1 text-sm text-gray-500 hover:text-gray-700"
          >
            Clear
          </button>
        )}
      </div>
    );
  };

  // Genres list
  const genres = [
    "Fiction",
    "Non-fiction",
    "Science",
    "Mystery",
    "Fantasy",
    "Biography",
    "History",
    "Self-Help",
    "Business",
    "Romance",
    "Thriller",
  ];

  return (
    <div className="w-full">
      {/* Search Input */}
      <div className="relative flex-grow max-w-md mb-4">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search size={20} className="text-gray-400" />
        </div>
        <input
          type="text"
          placeholder="Search books..."
          value={filters.search}
          onChange={handleSearchChange}
          className="pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 w-full text-base"
        />
      </div>

      {/* Genre Filter */}
      <div className="relative filter-dropdown mb-4">
        <select
          value={filters.genre}
          onChange={(e) => setFilters({ ...filters, genre: e.target.value })}
          className="w-full py-3 px-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 text-base appearance-none bg-white"
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>

      {/* Sort Filter */}
      <div className="relative filter-dropdown mb-4">
        <select
          value={filters.sort}
          onChange={handleSortChange}
          className="w-full py-3 px-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 text-base appearance-none bg-white"
        >
          <option value="title">Sort by Title</option>
          <option value="price">Sort by Price</option>
        </select>
      </div>

      {/* Price Range Filter */}
      <div className="relative filter-dropdown mb-4">
        <input
          type="number"
          value={filters.priceRange[0]}
          onChange={handlePriceChange}
          id="min-price"
          className="w-1/2 py-3 px-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 text-base"
        />
        -
        <input
          type="number"
          value={filters.priceRange[1]}
          onChange={handlePriceChange}
          id="max-price"
          className="w-1/2 py-3 px-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 text-base"
        />
      </div>

      {/* Rating Filter */}
      {renderStarRating()}

      {/* In Stock Filter */}
      <div className="flex items-center mb-4">
        <input
          type="checkbox"
          checked={filters.availability.inStock}
          onChange={handleAvailabilityChange}
          className="h-5 w-5 rounded border-gray-300 text-purple-600 focus:ring-purple-500"
        />
        <label htmlFor="inStock" className="ml-2 text-base text-gray-700">
          In Stock Only
        </label>
      </div>

      {/* Reset Filters */}
      <button
        onClick={resetFilters}
        className="text-sm text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg"
      >
        Clear All Filters
      </button>
    </div>
  );
}
