// UserContext.js
import React, { createContext, useState, useEffect } from 'react';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    // Check for token in localStorage
    const user = localStorage.getItem('user');
    
    if (!user) {
      setLoading(false);
      return;
    }
    
    // Fetch user profile
    const fetchUserProfile = async () => {
      try {
        const response = await fetch('https://localhost:7126/api/Auth/profile');
        
        if (!response.ok) {
          throw new Error('Failed to fetch user profile');
        }
        
        const userData = await response.json();
        setUser(userData);
      } catch (err) {
        console.error('Error fetching user profile:', err);
        setError(err.message);
        // Clear invalid token
        localStorage.removeItem('user');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserProfile();
  }, []);
  
  const login = async (email, password) => {
    try {
      const response = await fetch('https://localhost:7126/api/Auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Login failed');
      }
      
      const data = await response.json();
      
      // Save token
      localStorage.setItem('token', data.token);
      
      // Fetch user profile
      const profileResponse = await fetch('https://localhost:7126/api/Auth/profile');
      
      if (!profileResponse.ok) {
        throw new Error('Failed to fetch user profile');
      }
      
      const userData = await profileResponse.json();
      setUser(userData);
      
      return userData;
    } catch (err) {
      console.error('Login error:', err);
      throw err;
    }
  };
  
  const register = async (userData) => {
    try {
      const response = await fetch('https://localhost:7126/api/Auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Registration failed');
      }
      
      return await response.json();
    } catch (err) {
      console.error('Registration error:', err);
      throw err;
    }
  };
  
  const logout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };
  
  const updateProfile = async (updatedUserData) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Not authenticated');
      }
      
      const response = await fetch('https://localhost:7126/api/Auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
                },
        body: JSON.stringify(updatedUserData)
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to update profile');
      }
      
      const userData = await response.json();
      setUser(userData);
      
      return userData;
    } catch (err) {
      console.error('Update profile error:', err);
      throw err;
    }
  };
  
  return (
    <UserContext.Provider value={{ 
      user, 
      loading, 
      error, 
      login, 
      register, 
      logout, 
      updateProfile 
    }}>
      {children}
    </UserContext.Provider>
  );
};
