import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { BookmarksContext } from "../contexts/BookmarksContext"; // adjust the path
import { Trash2, ShoppingCart } from "lucide-react";

const BookmarksPage = () => {
  const { bookmarks, removeBookmark } = useContext(BookmarksContext);
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  // Fetch bookmarks when the component is mounted
  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("https://localhost:7126/api/Bookmarks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUser(JSON.parse(localStorage.getItem("user")));
      } catch (error) {
        setError("Failed to load bookmarks.");
      }
    };

    if (user) {
      fetchBookmarks();
    }
  }, [user]);

  // Function to remove a bookmark
  const handleRemoveBookmark = async (bookId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`https://localhost:7126/api/Bookmarks/${bookId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      removeBookmark(bookId); // Update local state after removing the bookmark
    } catch (error) {
      setError("Failed to remove bookmark.");
    }
  };

  return (
    <div className="container mx-auto py-6">
      <h2 className="text-3xl font-semibold">Your Bookmarks</h2>

      {error && <div className="text-red-500">{error}</div>}

      {bookmarks.length === 0 ? (
        <div className="text-center py-8">
          <ShoppingCart size={48} className="mx-auto text-gray-300 mb-4" />
          <h3 className="text-xl font-medium text-gray-900 mb-2">Your bookmarks are empty</h3>
          <p className="text-gray-500 mb-4">Start adding books to your bookmark list!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {bookmarks.map((book) => (
            <div key={book.id} className="border rounded-lg p-4 shadow-md">
              <img
                src={book.imageUrl || "/placeholder-image.png"}
                alt={book.title}
                className="w-full h-48 object-cover mb-4 rounded"
              />
              <h3 className="text-xl font-medium">{book.title}</h3>
              <p className="text-gray-600">{book.author}</p>
              <div className="flex justify-between items-center mt-4">
                <button
                  onClick={() => handleRemoveBookmark(book.id)}
                  className="text-red-600 hover:text-red-800"
                >
                  <Trash2 size={20} />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookmarksPage;
