import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BookDetails = () => {
  const { id } = useParams(); // Get the book id from the URL
  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Assume logged in for now, change based on actual auth logic

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`https://localhost:7126/api/Books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
        alert('Error fetching book details');
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await axios.get(`https://localhost:7126/api/Reviews/book/${id}`);
        setReviews(response.data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
        alert('Error fetching reviews');
      }
    };

    fetchBookDetails();
    fetchReviews();
  }, [id]); // Re-fetch book details and reviews when `id` changes

  const handleReviewSubmit = async () => {
    if (!isLoggedIn) {
      alert("Please log in to submit a review.");
      return;
    }

    try {
      await axios.post('https://localhost:7126/api/Reviews', { bookId: id, review });
      setReview('');
      alert("Review submitted successfully!");
      // Fetch reviews again to show the new one
      fetchReviews();
    } catch (error) {
      console.error('Error submitting review:', error);
      alert("Failed to submit review.");
    }
  };

  if (!book) return <div>Loading...</div>;

  return (
    <div className="max-w-2xl mx-auto py-10">
      {/* Book Title */}
      <h2 className="text-3xl font-semibold">{book.title}</h2>
      
      {/* Book Cover Image */}
      <img src={`https://localhost:7126${book.imageUrl}`} alt={book.title} className="w-full h-80 object-cover mb-4" />

      {/* Book Description */}
      <p className="text-gray-600">{book.description}</p>

      {/* Additional Book Info */}
      <div className="mt-6 text-lg">
        <p><strong>Author:</strong> {book.author}</p>
        <p><strong>Genre:</strong> {book.genre}</p>
        <p><strong>ISBN:</strong> {book.isbn}</p>
        <p><strong>Publisher:</strong> {book.publisher}</p>
        <p><strong>Language:</strong> {book.language}</p>
        <p><strong>Format:</strong> {book.format}</p>
        <p><strong>Pages:</strong> {book.pages}</p>
        <p><strong>Weight:</strong> {book.weight} kg</p>
        <p><strong>Dimensions:</strong> {book.dimensions}</p>

        {/* Book Price */}
        <p className="text-xl font-semibold mt-4">
          Price: ${book.price}
        </p>

        {/* Bestseller and New Release */}
        {book.isBestseller && <p className="text-green-600 mt-2">Bestseller</p>}
        {book.isNewRelease && <p className="text-blue-600 mt-2">New Release</p>}

        {/* Discount information */}
        {book.isOnSale && (
          <div className="text-red-600 mt-2">
            <p>On Sale!</p>
            <p>Discount: {book.discountPercentage}%</p>
            <p>Discount Valid From: {new Date(book.discountStartDate).toLocaleDateString()} To: {new Date(book.discountEndDate).toLocaleDateString()}</p>
          </div>
        )}
      </div>

      {/* Reviews Section */}
      <h3 className="mt-6 text-xl font-medium">Reviews</h3>
      {reviews.length === 0 ? (
        <p>No reviews yet. Be the first to review!</p>
      ) : (
        <div className="mt-4">
          {reviews.map((review) => (
            <div key={review.id} className="border-b py-2">
              <p className="font-semibold">{review.userName}</p>
              <p>{review.comment}</p>
            </div>
          ))}
        </div>
      )}

      {/* Review Submission */}
      {isLoggedIn && (
        <div className="mt-6">
          <textarea
            value={review}
            onChange={(e) => setReview(e.target.value)}
            className="w-full h-32 border p-4"
            placeholder="Write a review..."
          ></textarea>
          <button onClick={handleReviewSubmit} className="bg-blue-600 text-white px-6 py-2 mt-4 rounded">
            Submit Review
          </button>
        </div>
      )}
    </div>
  );
};

export default BookDetails;
