import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BookDetails = () => {
  const { bookId } = useParams();
  const [book, setBook] = useState(null);
  const [review, setReview] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`https://localhost:7126/api/Books/${bookId}`);
        setBook(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };
    fetchBookDetails();
  }, [bookId]);

  const handleReviewSubmit = async () => {
    // Add logic to submit the review
    if (isLoggedIn) {
      try {
        await axios.post(`https://localhost:7126/api/Reviews`, { bookId, review });
        setReview('');
      } catch (error) {
        console.error('Error submitting review:', error);
      }
    }
  };

  if (!book) return <div>Loading...</div>;

  return (
    <div className="max-w-2xl mx-auto py-10">
      <h2 className="text-3xl font-semibold">{book.title}</h2>
      <img src={book.coverImageUrl} alt={book.title} className="w-full h-80 object-cover mb-4" />
      <p className="text-gray-600">{book.description}</p>

      {isLoggedIn && (
        <div className="mt-6">
          <textarea
            value={review}
            onChange={(e) => setReview(e.target.value)}
            className="w-full h-32 border p-4"
            placeholder="Write a review..."
          ></textarea>
          <button onClick={handleReviewSubmit} className="bg-blue-600 text-white px-6 py-2 mt-4 rounded">
            Submit Review
          </button>
        </div>
      )}
    </div>
  );
};

export default BookDetails;
