import { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"

const BookDetails = () => {
  const { id } = useParams() // Get the book id from the URL
  const [book, setBook] = useState(null)
  const [reviews, setReviews] = useState([])
  const [review, setReview] = useState("")
  const [isLoggedIn, setIsLoggedIn] = useState(true) // Assume logged in for now, change based on actual auth logic
  const [isSubmitting, setIsSubmitting] = useState(false)

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`https://localhost:7126/api/Books/${id}`)
        setBook(response.data)
      } catch (error) {
        console.error("Error fetching book details:", error)
        alert("Error fetching book details")
      }
    }

    const fetchReviews = async () => {
      try {
        const response = await axios.get(`https://localhost:7126/api/Reviews/book/${id}`)
        setReviews(response.data)
      } catch (error) {
        console.error("Error fetching reviews:", error)
        alert("Error fetching reviews")
      }
    }

    fetchBookDetails()
    fetchReviews()
  }, [id]) // Re-fetch book details and reviews when `id` changes

  const handleReviewSubmit = async () => {
    if (!isLoggedIn) {
      alert("Please log in to submit a review.")
      return
    }

    if (!review.trim()) {
      alert("Please write a review before submitting.")
      return
    }

    setIsSubmitting(true)
    try {
      await axios.post("https://localhost:7126/api/Reviews", { bookId: id, review })

      // Refresh reviews after submission
      const response = await axios.get(`https://localhost:7126/api/Reviews/book/${id}`)
      setReviews(response.data)

      setReview("")
      alert("Review submitted successfully!")
    } catch (error) {
      console.error("Error submitting review:", error)
      alert("Failed to submit review.")
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!book) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-pulse flex flex-col items-center">
          <div className="h-32 w-32 bg-gray-200 rounded-full mb-4"></div>
          <div className="h-4 w-48 bg-gray-200 rounded mb-2"></div>
          <div className="h-3 w-32 bg-gray-200 rounded"></div>
        </div>
      </div>
    )
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8 bg-white">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Left Column - Book Image */}
        <div className="md:col-span-1">
          <div className="sticky top-8">
            <div className="rounded-lg overflow-hidden shadow-lg">
              <img
                src={`https://localhost:7126${book.imageUrl}`}
                alt={book.title}
                className="w-full h-auto object-cover"
              />
            </div>

            {/* Price and Purchase Section */}
            <div className="mt-6 bg-gray-50 rounded-lg p-6 shadow-md">
              <div className="flex items-baseline justify-between">
                <span className="text-3xl font-bold text-gray-900">${book.price}</span>

                {book.isOnSale && (
                  <span className="bg-red-100 text-red-800 text-xs font-semibold px-2.5 py-0.5 rounded">
                    {book.discountPercentage}% OFF
                  </span>
                )}
              </div>

              {book.isOnSale && (
                <div className="mt-2 text-sm text-gray-600">
                  <p>Sale ends: {new Date(book.discountEndDate).toLocaleDateString()}</p>
                </div>
              )}

              <div className="mt-4 space-y-3">
                <button className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg transition duration-150 ease-in-out">
                  Add to Cart
                </button>
                <button className="w-full bg-gray-200 hover:bg-gray-300 text-gray-800 font-medium py-3 px-4 rounded-lg transition duration-150 ease-in-out">
                  Add to Wishlist
                </button>
              </div>

              {/* Badges */}
              <div className="mt-4 flex flex-wrap gap-2">
                {book.isBestseller && (
                  <span className="bg-yellow-100 text-yellow-800 text-xs font-semibold px-2.5 py-0.5 rounded-full">
                    Bestseller
                  </span>
                )}
                {book.isNewRelease && (
                  <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2.5 py-0.5 rounded-full">
                    New Release
                  </span>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Right Column - Book Details and Reviews */}
        <div className="md:col-span-2">
          {/* Book Title and Author */}
          <div className="border-b pb-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">{book.title}</h1>
            <p className="text-xl text-gray-600">
              by <span className="font-semibold">{book.author}</span>
            </p>
          </div>

          {/* Book Description */}
          <div className="py-6 border-b">
            <h2 className="text-xl font-semibold mb-4">Description</h2>
            <p className="text-gray-700 leading-relaxed">{book.description}</p>
          </div>

          {/* Book Details */}
          <div className="py-6 border-b">
            <h2 className="text-xl font-semibold mb-4">Product Details</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <div className="space-y-2">
                <p className="flex justify-between">
                  <span className="text-gray-600">Genre:</span>
                  <span className="font-medium">{book.genre}</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">ISBN:</span>
                  <span className="font-medium">{book.isbn}</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">Publisher:</span>
                  <span className="font-medium">{book.publisher}</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">Language:</span>
                  <span className="font-medium">{book.language}</span>
                </p>
              </div>
              <div className="space-y-2">
                <p className="flex justify-between">
                  <span className="text-gray-600">Format:</span>
                  <span className="font-medium">{book.format}</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">Pages:</span>
                  <span className="font-medium">{book.pages}</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">Weight:</span>
                  <span className="font-medium">{book.weight} kg</span>
                </p>
                <p className="flex justify-between">
                  <span className="text-gray-600">Dimensions:</span>
                  <span className="font-medium">{book.dimensions}</span>
                </p>
              </div>
            </div>
          </div>

          {/* Reviews Section */}
          <div className="py-6">
            <h2 className="text-xl font-semibold mb-4">Customer Reviews</h2>

            {reviews.length === 0 ? (
              <div className="bg-gray-50 rounded-lg p-6 text-center">
                <p className="text-gray-600">No reviews yet. Be the first to review!</p>
              </div>
            ) : (
              <div className="space-y-6">
                {reviews.map((review) => (
                  <div key={review.id} className="bg-gray-50 rounded-lg p-6">
                    <div className="flex items-center mb-2">
                      <div className="h-10 w-10 rounded-full bg-gray-300 flex items-center justify-center text-gray-600 font-bold">
                        {review.userName ? review.userName.charAt(0).toUpperCase() : "U"}
                      </div>
                      <div className="ml-4">
                        <p className="font-semibold text-gray-900">{review.userName || "Anonymous"}</p>
                        <p className="text-sm text-gray-500">
                          {review.date ? new Date(review.date).toLocaleDateString() : "Unknown date"}
                        </p>
                      </div>
                    </div>
                    <p className="text-gray-700">{review.comment}</p>
                  </div>
                ))}
              </div>
            )}

            {/* Review Submission Form */}
            {isLoggedIn ? (
              <div className="mt-8 bg-gray-50 rounded-lg p-6">
                <h3 className="text-lg font-semibold mb-4">Write a Review</h3>
                <textarea
                  value={review}
                  onChange={(e) => setReview(e.target.value)}
                  className="w-full h-32 border border-gray-300 rounded-lg p-4 focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                  placeholder="Share your thoughts about this book..."
                ></textarea>
                <button
                  onClick={handleReviewSubmit}
                  disabled={isSubmitting}
                  className="mt-4 bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-6 rounded-lg transition duration-150 ease-in-out disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isSubmitting ? "Submitting..." : "Submit Review"}
                </button>
              </div>
            ) : (
              <div className="mt-8 bg-gray-50 rounded-lg p-6 text-center">
                <p className="text-gray-600 mb-4">Please log in to leave a review.</p>
                <button className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-6 rounded-lg transition duration-150 ease-in-out">
                  Log In
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

export default BookDetails
